import React,{Component} from 'react';

import './IranMap.css';

import CityModal from './CityModal';


class IranMap extends Component {

    constructor(props) {
        super(props);
        this.state = {
            citiesData: null,
            selectedCity: null,
            isModalOpen: false,
            isLoading: false,
        };
    }
    // fetchTableData =  async  () => {
    //     const URL = "localhost:9000/cities/"
    //     const response = await fetch(URL)
    //     const users = await response.json()
    // }
    componentDidMount(){
      this.setState({ isLoading: true });
      fetch("http://localhost:9000/cities/")
      .then(response => response.json())
      .then(data => this.setState({ citiesData: data, isLoading: false }));

    }



    cityClicked = (id) => (event) => {
        event.preventDefault();
        // Fetch city details and open modal
    };

    closeModal = () => {
        this.setState({
            isModalOpen: false,
        });
    };

    render() {
        if (tiisLoading) {
            return <p>Loading ...</p>;
          }
        return (
            <div>
                <div className="map-container">
                    {console.log(this.state.citiesData)}
                    {(this.state.citiesData || []).map((record) => (
                        <div
                            key={record.id}
                            className="city-name"
                            style={{
                                top: `${record.top}%`,
                                left: `${record.left}%`,
                            }}
                            onClick={this.cityClicked(record.id)}
                        >
                            {record.name}
                        </div>
                    ))}
                </div>
                <CityModal
                    city={this.state.selectedCity}
                    isOpen={this.state.isModalOpen}
                    onClose={this.closeModal}
                />
            </div>
        );
    }
}

export default IranMap;
