import React from 'react';

import './IranMap.css';

import CityModal from './CityModal';


class IranMap extends React.Component {

    state = {
        citiesData: null,
        selectedCity: null,
        isModalOpen: false,
    };

    componentDidMount(){
        // fetchTableData()
      fetch("localhost:9000/cities/")
      .then(response => response.json())
      .then(data => this.setState({ citiesData: data.citiesData }));
    }
